import details from '../pages/Home';
console.log(details);
const lat = '';
const lon = '';
export default async function coordinates() {
  // GeoApify gets Lat/Long
  fetch(`https://api.geoapify.com/v1/geocode/search?city=${details.city}&state=${details.state}&format=json&apiKey=27a84d7b0c1b4d52b41acc3e82bbe239`).then(res => res.json()).then(res => lat.push(res.lat.value)).then(res => lon.push(res.lon.value));
}
