import details from '../pages/Home'


export default async function coordinates (detail) {
    
    const lat = ''
    const lon = ''

    const details = {
        city:details.city,
        state: details.state
    }
    console.log(details)
   
    // GeoApify gets Lat/Long
    await fetch(`https://api.geoapify.com/v1/geocode/search?city=${details.city}&state=${details.state}&format=json&apiKey=27a84d7b0c1b4d52b41acc3e82bbe239`)
    .then(res => res.json())
    .then(res => lat.push(res.lat.value))
    .then(res => lon.push(res.lon.value))
}
